import React from "react";
import Code from "../interfaces/code";
// import {Prism as SyntaxHighlighter} from "@types/react-syntax-highlighter";
const {Prism} = require("@types/react-syntax-highlighter");

const CodeBlock: React.FunctionComponent<Code> = ({value, language}) => {
	return <Prism language={language}>{value}</Prism>;
};

export default CodeBlock;
