import React from "react";
import fs from "fs";

const Post = ({article}) => {
	return <div>The article for this page is {article}</div>;
};

export async function getStaticPaths() {
	// console.log("current folder name: " + __dirname);
	const files = fs.readdirSync("src/contents/articles");
	const filenames = files.map(filename => ({
		params: {
			article: filename.replace(".md", ""),
		},
	}));
	console.log(filenames);
	return {
		path: filenames,
		fallback: false,
	};
}

export async function getStaticProps({params: {article}}) {
	return {
		props: {
			article,
		},
	};
}

export default Post;
