import Navbar from "../components/Navbar";
import {GetStaticProps} from "next";
import fs from "fs";
import path from "path";
// import Files from "../interfaces/files";
import Link from "next/link";
import TopLevelContainer from "../components/TopLevelContainer";
import matter from "gray-matter";
import React from "react";
// import Metadata from '../interfaces/metadata'

const Articles: React.FunctionComponent<any> = ({titles}) => (
	<>
		<Navbar title="Articles"></Navbar>
		<TopLevelContainer title="Articles">
			<h1> Articles </h1>
			<div>
				{titles.map(title => {
					return (
						<li key={title}>
							<Link href="/articles/[slug]" as={"/articles/" + title}>
								<a>{title}</a>
							</Link>
						</li>
					);
				})}
			</div>
		</TopLevelContainer>
	</>
);

export default Articles;

export const getStaticProps: GetStaticProps = async () => {
	// const files = fs.readdirSync(path.join("pages", "articles", "contents"));
	// return {
	// 	props: {
	// 		filenames: files.map(filename => filename.replace(".md", "")),
	// 	},
	// };
	const dirPath = path.join("pages", "articles", "contents");
	const metadatas = fs
		.readdirSync(dirPath)
		.map(filename => fs.readFileSync(path.join(dirPath, filename)).toString())
		.map(data => matter(data).data);
	const [articleMetadataList] = React.useState(metadatas);
	const titles = articleMetadataList.map(metadata => metadata.title);
	console.log(titles);
	return {
		props: {
			titles: titles,
		},
	};
};
