import fs from "fs";
import {GetStaticPaths, GetStaticProps} from "next";
import React from "react";
import Article from "../../interfaces/article";
import path from "path";

const Post: React.FunctionComponent<Article> = ({slug}) => {
	return <div>The slug for this page is {slug}</div>;
};

export default Post;

export const getStaticPaths: GetStaticPaths = async () => {
	const files = fs.readdirSync("pages/articles/contents");
	return {
		paths: files.map(filename => ({
			params: {
				slug: filename.replace(".md", ""),
			},
		})),
		fallback: false,
	};
};

export const getStaticProps: GetStaticProps = async ctx => {
	if (!ctx.params) {
		return {props: {}};
	}
	const {slug} = ctx.params;
	const filename = slug instanceof Array ? slug.join() : slug;
	const contents = fs.readFileSync(
		path.join("pages", "articles", "contents", filename + ".md")
	);
	console.log(contents);
	return {
		props: {
			contents,
		},
	};
};
